def fund_issue(private_key, eth_account, eth_provider, to, ammount=1):
    provider = HTTPProvider(eth_provider)
    web3 = Web3(provider)
    print('balance: ', web3.eth.getBalance(eth_account))
    token_contract = web3.eth.contract(address='0xc55cF4B03948D7EBc8b9E8BAD92643703811d162', abi=token_abi)
    print('balance SNT: ', token_contract.functions.balanceOf('0x6f00cE7253bFD3A5A1c307b5E13814BF3433C72f').call())

    transaction = token_contract.functions.transfer(to, ammount).buildTransaction({
        'from': eth_account,
        'nonce': web3.eth.getTransactionCount(eth_account) + 1,
        'gas': 300459,
        'gasPrice': 5000000000,
        'value': 0
    })
    print(transaction)
    signed = web3.eth.account.signTransaction(transaction, private_key)
    print(signed)
    print('send:')
    print(web3.eth.sendRawTransaction(signed.rawTransaction).hex())

